import datetime

# make counts for each month)
month_mask = '20xx-xx'
plot_counts = {}

def mask_date(date):
    return date[:len(month_mask)]# strip some month data to group by month

def make_summary_dict_entry(date):
    if date not in plot_counts:
        plot_counts[date] = {}
        plot_counts[date]['Tagged Python 2'] = 0
        plot_counts[date]['Tagged Python 3'] = 0
        plot_counts[date]['Published to PyPI'] = 0        

date_now = mask_date(str(datetime.datetime.utcnow()))

for i in python2_start_stripped:
    date = mask_date(i)
    if not date == date_now:
        # filter out the current month as it is partial and
        # will make the chart 'fall off'
        make_summary_dict_entry(date)
        plot_counts[date]['Tagged Python 2'] += 1

for i in python3_start_stripped:
    date = mask_date(i)
    if not date == date_now:
        make_summary_dict_entry(date)
        plot_counts[date]['Tagged Python 3'] += 1

for i in first_published_stripped:
    date = mask_date(i)
    if not date == date_now:
        make_summary_dict_entry(date)
        plot_counts[date]['Published to PyPI'] += 1

# Process Version Table.
version_plot_counts = {}

def make_version_dict_entry(date):
    if date not in version_plot_counts:
        version_plot_counts[date] = dict()
        version_plot_counts[date]['Python 2 (Only)'] = 0
        version_plot_counts[date]['Python 3 (Only)'] = 0
        version_plot_counts[date]['Polyglot (Both)'] = 0
        version_plot_counts[date]['Polyglot + Python 2'] = 0
        version_plot_counts[date]['Polyglot + Python 3'] = 0

